// SPDX-License-Identifier: MIT

pragma solidity ^0.7.0;

contract Election{
    
    //struct candidate
    struct Candidate{
        uint id; //Candidate id
        string name;
        uint voteCount; //votes won by the candidate 
    }
    
    //candidate count 
    uint public candidateCount;
    
    // map candidate info with their IDs
    mapping(uint => Candidate) public candidateArray;
    
    // map to check if person has voted or not
    mapping(address => bool) public votedOrNot;
    
    //update ui event
    event hasVoted(
        uint id,
        string name,
        uint voteCount);
    
    constructor(){
        //aading candidates
        addCandidate("Rachel");
        addCandidate("James");
    }
    
    //add Candidate to Blockchain
    function addCandidate(string memory name) private{
        candidateCount++;
        candidateArray[candidateCount] = Candidate(candidateCount,name,0);
    }
    
    //vote Candidate
    function castVote(uint _id) public {
        
        //check if person is not voting second time
        require(!votedOrNot[msg.sender], 'You can vote only once');
        
        //check Candidate with selected id is valid
        require(_id > 0 && _id <= candidateCount, 'The selected Candidate is not available');
        
        //increase vote count
        candidateArray[_id].voteCount++;
        
        votedOrNot[msg.sender] = true;
        
        //emit event data
        emit hasVoted(_id, candidateArray[_id].name, candidateArray[_id].voteCount);        
    }
    
}
